import React, { useState, useEffect } from "react";
import { useParams } from "react-router";
import axios from "axios";

const API_BASE_URL = "http://localhost:8080/";
const API_FILMS_URL = `${API_BASE_URL}films/`;

const Film = ({ match }) => {
  const [data, setData] = useState({});
  let {id} = useParams();


  useEffect(() => {
	getFilmByID();
  }, []);

  const getFilmByID = async () => {
	try {
	  const res = await axios.get(`${API_FILMS_URL}${id}`);
	  console.log(`${API_FILMS_URL}${id}`);
	  setData(res.data);
	  console.log(res.data);
	} catch (err) {
	  console.error(err);
	}
  };

  return (
	<div>
		{data && 
			<div className="item-wrapper" key={data.episode_id}>
				<div>
					{/* <img className="prod-image" src={} alt="" /> */}
				</div>
				<div>
					<h1 className="brand">{data.title}</h1>
					<h2>Director: {data.director}</h2>
					<h2>Producer: {data.producer}</h2>
					<p>{data.opening_crawl}</p>
				</div>
			</div>
		}
	</div>
  );
};

export default Film;
