import React, { useState, useEffect } from "react";
import { useParams } from "react-router";
import axios from "axios";

const API_BASE_URL = "http://localhost:8080/";
const API_FILMS_URL = `${API_BASE_URL}films/`;

const Film = ({ match }) => {
  const [data, setData] = useState([]);
  let {id} = useParams();


  useEffect(() => {
	getFilmByID();
  }, []);

  const getFilmByID = async () => {
	try {
	  const res = await axios.get(`${API_FILMS_URL}?id=${id}`);
	  console.log(`${API_FILMS_URL}?id=${id}`);
	  setData(res.data.results);
	  console.log(res.data.results);
	} catch (err) {
	  console.error(err);
	}
  };

  return (
	<div>
		{data?.map((item) => {
			return (
				<div className="product-container" key={item.episode_id}>
					<div>
						{/* <img className="prod-image" src={} alt="" /> */}
					</div>
					<div>
						<h1 className="brand">{item.title}</h1>
						<h2>Director: {item.director}</h2>
						<h2>Producer: {item.producer}</h2>
						<p>{item.opening_crawl}</p>
					</div>
				</div>
			);
		})}
	</div>
  );
};

export default Film;
